INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30001', '124 Spider', '123', '250', '1.124', 'RWD', '2017-04-22', 'The 124 Spider was first introduced in 2016 as a homage to the 50th anniversary of the 1966-1985 124 Sport Spider.', '30001.png', '10001');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30002', '695 Biposto', '139', '249', '0.997', 'FWD', '2016-12-05', 'Using technology derived from the 695 Assetto Corse racing car series, it is currently the fastest production car manufactured by Fiat\'s performance division Abarth.', '30002.png', '10001');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30003', 'Fiat 131', '104', '176', '0.98', 'RWD', '1980-03-21', 'Carlo Abarth is rightfully famous for applying the dark art of tuning to some of the most unlikely cars in the world—simple, robust, but decidedly unsporting Fiats—and creating legitimate giant-slaying racers out of them.', '30003.png', '10001');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30004', '595 Esseesse', '21', '46', '0.57', 'RWD', '1968-06-15', 'What it lacks in power and refinement it makes up for in character and likability. The Abarth 500 is an reverse-engineered VW Bug designed by Italians for Italians.', '30004.png', '10001');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30005', 'NSX', '427', '845', '1.725', 'AWD', '2017-08-22', 'Sold as a Honda model outside of North America, the 2017 NSX is an AWD hybrid supercar that succeeds the original NSX produced in Japan between 1990 and 2005.', '30005.png', '10002');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30006', 'RSX Type-S', '149', '193', '1.279', 'FWD', '2002-04-25', 'The Acura RSX is the successor to the Acura Integra that was sold in North America and Hong Kong, although it was sold as the Honda Integra in Japan and Australia.', '30006.png', '10002');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30007', 'Integra Type-R', '145', '176', '1.197', 'FWD', '2001-01-13', 'The Type-R is the top-of-the-line model of the last generation Acura Integra, thoroughly reengineered for maximal handling and greater speed.', '30007.png', '10002');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30008', 'Stelvio Quadrifoglio', '337', '600', '1.978', 'AWD', '2018-12-12', 'The 2018 Alfa Romeo Stelvio Quadrifoglio, abbreviated as Alfa Stelvio, is a performance SUV by Alfa Romeo', '30008.png', '10003');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30009', 'Giulia Quadrifoglio', '377', '600', '1.701', 'RWD', '2017-08-15', 'The Giulia Quadrifoglio is the high-performance model of the new Giulia lineup and the first model of the 952-codenamed Giulia range to be unveiled.', '30009.png', '10003');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30010', '4C', '179', '323', '0.94', 'RWD', '2014-01-09', 'The 4C is a lightweight sports car by Alfa Romeo and the second Alfa Romeo car to re-enter into the United States market since 2008 after the 8C Competizione.', '30010.png', '10003');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30011', '8C Competizione', '336', '480', '1.585', 'RWD', '2007-05-03', 'The Alfa Romeo 8C Competizione is a sports car produced between 2007 and 2009 with a total production number of 500 units. Alfa Romeo shares the platform previously utilised by the Maserati Coupe.', '30011.png', '10003');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30012', '33 Stradale', '183', '201', '0.7', 'RWD', '1968-10-23', 'One of the most beautiful cars ever built, the lightweight and powerful Alfa Romeo 33 Stradale also holds the record of being the most expensive new car sold in the U.S. of its time', '30012.png', '10003');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30013', 'Giulia TZ2', '127', '176', '0.62', 'RWD', '1965-03-14', 'Based on the success of the TZ1, a lightweight, space-frame race car, the TZ2 was lighter, lower and more powerful. Both cars were built by the team at Autodelta made up of owner Carlo Chiti, and design engineers Oarzio Satta and Guisseppe Busso.', '30013.png', '10003');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30014', 'Giulia Sprint GTA Stradale', '86', '142', '0.76', 'RWD', '1965-06-07', 'The Alfa Romeo Giulia Sprint GTA Stradale is a performance model of the Giulia coupé that was produced from 1963 to 1977, succeeding the 1958 Alfa Romeo Giulietta Sprint Veloce.', '30014.png', '10003');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30015', 'Class 10 Race Car', '146', '258', '0.998', 'RWD', '2015-08-14', 'Consistency on any terrain is what the Alumi Craft Class 10 Race Car is built for. With a long travel suspension and best-in-class durability, it’s no wonder the top names in desert racing worldwide race Alumi Craft Class 10s.', '30015.png', '10004');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30016', 'Germlin X', '112', '332', '1.288', 'RWD', '1973-07-19', 'Described by AMC as “the first American-built import,” and based on an AMC chief stylist’s sketch on an air-sickness bag, the AMC Gremlin was a two-fold response both to Japanese imports of the time and to the fuel crisis.', '30016.png', '10005');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30017', 'Javelin AMX', '246', '584', '1.563', 'RWD', '1971-11-03', 'Those huge fender arches? They’re to fit oversize race tires. The long deck? That’s for the 401 CID, Trans-Am-inspired V8 packing a 4-barrel carb good for 330 horsepower.', '30017.png', '10005');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30018', 'M12S Warthog CST', '537', '1160', '2.3', 'AWD', '2022-12-04', 'The M12S Warthog CST is a 33% smaller, 50% faster rendition of the M12B Force Application Vehicle utilized by the United Nations Space Command, fitted with a smaller displacement 8 liter twin-turbocharged V8.', '30018.png', '10006');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30019', 'NOMAD', '176', '300', '0.67', 'RWD', '2016-02-15', 'Built on an Ariel Atom steel-tube chassis, it is powered by a Honda-sourced 2.4L (2,354 cc) iVTEC four-cylinder engine with power and torque rated at 236 hp (176 kW) and 221 ft·lb (300 N·m), respectively.', '30019.png', '10007');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30020', 'KZ1R', '388', '540', '1.35', 'RWD', '2012-09-16', 'A track-tuned version of the race-bred supercar of the road, the KZ1R is capable of a top speed of 200 mph and 0-60 acceleration of three seconds.', '30020.png', '10008');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30021', 'Vantage', '375', '685', '1.586', 'RWD', '2018-06-18', 'The 2018 Aston Martin Vantage, abbreviated as AM Vantage \'18, is a FR sports car by Aston Martin', '30021.png', '10009');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30022', 'DB11', '453', '700', '1.875', 'RWD', '2017-12-23', 'Built on an all-new aluminum platform replacing the VH platform that was last seen in the Aston Martin Vanquish, the DB11 is the successor to the DB9.', '30022.png', '10009');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30023', 'Vulcan', '611', '800', '1.36', 'RWD', '2016-09-26', 'Based on the Aston Martin One-77, the Vulcan is a track-only hypercar by Aston Martin. Its name is a reference to the British Cold War-era bombers of the same name.', '30023.png', '10009');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30024', 'Vantage GT12', '422', '626', '1.565', 'RWD', '2016-09-14', 'The GT12 is an extreme, limited-production version of the V12 Vantage S, limited to only 100 units. Originally named the Aston Martin Vantage GT3, it was renamed after Porsche complained over the use of the \"GT3\" moniker.', '30024.png', '10009');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30025', 'V12 Vantage S', '421', '620', '1.665', 'RWD', '2013-07-23', 'The V12 Vantage S is a more lighter and powerful paddle-shifter variant of the V12 Vantage that debuted for the 2013 model year.', '30025.png', '10009');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30026', 'Vanquish', '421', '620', '1.739', 'RWD', '2012-12-21', 'The Vanquish was released for the 2012 model year to replace the DBS as the top-ranging model of Aston Martin\'s series production cars.', '30026.png', '10009');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30027', 'One-77', '559', '750', '1.5', 'RWD', '2010-08-11', 'Designed as a halo car, the One-77 is currently the fastest road car made by Aston Martin due to being capable of reaching 220 mph (354 km/h) as top speed.', '30027.png', '10009');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30028', 'DB5', '242', '391', '1.465', 'RWD', '1964-01-01', 'The DB5 Vantage is the most powerful model of the DB5 series and was manufactured in a limited production run of 25 units.', '30028.png', '10009');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30029', 'DBR1', '189', '335', '0.875', 'RWD', '1958-03-01', 'A legend among Astons’s [sic] and as primal as they come. Before there were such things as seat belts and safety concerns there were cars like the DBR1.', '30029.png', '10009');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30030', 'R8 V10 Plus', '455', '560', '1.555', 'AWD', '2016-04-17', 'The R8 V10 plus is part of the second generation of the Audi R8 supercar, and the successor to the 2013 Audi R8 Coupé V10 plus 5.2 FSI quattro. It shares its platform with the Lamborghini Huracán LP 610-4.', '30030.png', '10010');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30031', 'RS 6 Avant', '412', '700', '1.935', 'AWD', '2015-05-14', 'The 2015 RS 6 Avant, codenamed C7, is the third iteration of the Audi RS 6 and the successor to the 2009 RS 6.', '30031.png', '10010');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30032', 'TTS Coupe', '231', '380', '1.385', 'AWD', '2015-03-18', 'Finally, a new Audi TT is available in Forza! The TTS is an elite example of VW’s MQB platform (a strategy designed to reduce costs and shorten production times) but, at its heart, it’s a thrilling, eminently drivable Audi experience.', '30032.png', '10010');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30033', 'S1', '170', '327', '1.315', 'AWD', '2015-06-16', 'The S1 rivals against the Mini John Cooper Works GP and other supermini hot hatches. It began production in 2014 and is available as a three-door or five-door hatchback.', '30033.png', '10010');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30034', 'R8 Coupe V10 Plus Quattro', '404', '540', '1.67', 'AWD', '2013-11-28', 'The 2013 Audi R8 replaces the Audi R8 5.2 FSI quattro. It was succeeded by the Audi R8 V10 plus in 2015.', '30034.png', '10010');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30035', 'RS 7 Sportback', '412', '750', '1.955', 'AWD', '2013-02-15', 'The RS 7 Sportback is the liftback/hatchback variant of the Audi RS 6 Avant. It started production in 2013 and was replaced by the more powerful \'Performance\' entitled model in 2017.', '30035.png', '10010');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30036', 'RS 4 Avant \'13', '331', '430', '1.795', 'AWD', '2013-07-14', 'The RS 4 Avant is based on the B8 generation Audi S4, built using the Volkswagen MLB platform.', '30036.png', '10010');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30037', 'RS 5 Coupe', '330', '430', '1.737', 'AWD', '2011-08-08', 'Initially intended to replace the 2006 Audi RS 4,the RS 5 is a top ranging performance car priced above the Audi S5 that competes against high-performance compact executive cars such as the fourth generation BMW M3 Coupé.', '30037.png', '10010');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30038', 'RS 3 Sportback', '250', '451', '1.575', 'AWD', '2011-10-25', 'Following the debut of the first generation Audi TT RS, the RS 3 Sportback was the second high performance RS badged sports compact to be released by Audi.', '30038.png', '10010');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30039', 'RS 6 \'09', '425', '648', '1.985', 'AWD', '2009-03-22', 'The 2009 RS 6 is the second iteration of the Audi RS 6, which is based on the C6 generation Audi A6.', '30039.png', '10010');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30040', 'RS 4', '313', '430', '1.65', 'AWD', '2006-04-12', 'The second generation Audi RS 4, codenamed B7, replaces the B5 generation that was last produced in 2001. It turned away from forced induction by switching to a high-revving naturally aspirated V8 engine.', '30040.png', '10010');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30041', 'RS 6 \'03', '336', '563', '1.825', 'AWD', '2003-03-19', 'The 2003 RS 6 was the flagship model of the C5 generation Audi A6 and Audi\'s first model to bear the RS 6 badge. It was also the company\'s first car with a turbocharged eight-cylinder engine.', '30041.png', '10010');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30042', 'RS 4 Avant \'01', '280', '440', '1.62', 'AWD', '2001-01-11', 'Designed as the successor to the 1995 RS 2 Avant, the RS 4 Avant was only available as a station wagon but solely developed by Audi\'s subsidiary quattro GmbH.', '30042.png', '10010');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30043', 'RS 2 Avant', '232', '410', '1.595', 'AWD', '1995-03-01', 'The RS 2 Avant was designed and manufactured in cooperation with Porsche. It was introduced as Audi\'s first high-performance station wagon and as the first RS', '30043.png', '10010');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30044', 'Audi Sport Quattro S1', '350', '480', '1.09', 'AWD', '1986-06-16', 'The Audi Sport quattro S1 competed in Group B class of the 1986 World Rally Championship season.', '30044.png', '10010');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30045', '3000 MKIII', '110', '235', '1.181', 'RWD', '1965-10-14', 'The beauty that is embodied by the Austin-Healy [sic] 3000 MkIII is equaled only by similarly hand-crafted boats and motorcycles of the era constructed by skilled craftsmen who put their heart and soul into everything they built.', '30045.png', '10011');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30046', 'Sprite MKI', '34', '71', '0.664', 'RWD', '1958-04-18', 'The Sprite MKI was commonly known as the Bugeye Sprite in the U.S., or Frogeye Sprite in Europe due to the bulging headlights in the bonnet.', '30046.png', '10011');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30047', 'Type D', '362', '560', '1.277', 'RWD', '1939-06-17', 'The Type D was a Grand Prix race car whose success in state-sponsored motorsport ended when World War II began. It was the successor to the Type C.', '30047.png', '10012');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30048', 'MONO', '209', '280', '0.614', 'RWD', '2014-12-12', 'The Mono was designed by Briggs Automotive Company (BAC) along with several automobile engineering companies including Cosworth.', '30048.png', '10013');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30049', 'Continential Supersports', '522', '1017', '2.281', 'AWD', '2017-11-10', 'Succeeding the 2010 Supersports, the 2017 Supersports is a W12-engined grand tourer based on the facelifted 2015 Bentley Continental GT.', '30049.png', '10014');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30050', 'Bentayga', '447', '900', '2.422', 'AWD', '2016-10-09', 'In an age dominated by SUVs, the luxurious and powerful Bentayga is the crown prince of them all.', '30050.png', '10014');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30051', 'Continential GT Speed', '460', '800', '2.32', 'AWD', '2013-03-03', 'The Continental GT Speed debuted for the 2014 model year as a high-end trim level of the second generation Bentley Continental that was produced from 2011 to 2018.', '30051.png', '10014');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30052', '8-Liter', '164', '520', '2.54', 'RWD', '1931-01-02', 'The Bentley 8-Liter (or 8-Litre as it was actually called) was created by Bentley Motors Limited as a luxury model and was the last full production new model vehicle made by the company while still under that name.', '30052.png', '10014');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30053', 'M5 \'18', '447', '750', '1.982', 'AWD', '2018-02-03', 'The F90 codenamed BMW M5 is the sixth generation of the BMW M5 super saloon, replacing the F10 series for the 2018 model year.', '30053.png', '10015');
