INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30056', 'M2 Coupe', '272', '501', '1.565', 'RWD', '2016-09-13', 'The F87-codenamed BMW M2, the successor to the BMW 1 Series M Coupe, is a high-performance variant of the BMW 2 Series. It was unveiled in October 2015 and began production in late 2015.', '30056.png', '10015');
INSERT INTO `carsdb`.`product` (`productid`, `productname`, `power`, `torque`, `weight`, `transmission`, `yom`, `description`, `img`, `brand_brandid`) VALUES ('30057', 'I8', '266', '570', '1.533', 'AWD', '2015-11-24', 'The i8, internally codenamed I12, is a plug-in hybrid supercar developed by BMW for its hybrid model range \"BMW i.\"', '30057.png', '10015');
